import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
const BlogDetails = () => {
  const [blogDetails, setBlogDetails] = useState(null);
  const { id } = useParams();
  const fetchData = useCallback(async () => {
    try {
      const showAllBlog = await fetch(`http://localhost:8002/api/get-blog/${id}`);
      if (!showAllBlog.ok) {
        throw new Error("Failed to fetch data");
      }
      const result = await showAllBlog.json();
      setBlogDetails(result);
    } catch (error) {
      console.error(error);
    }
  }, [id]);
  useEffect(() => {
    fetchData();
  }, [fetchData]);
  if (!blogDetails) {
    return <p>Loading...</p>;
  }
  return (
    <div className="main-blog-details">
      <h2>{blogDetails.title}</h2>
      <div className="portfolio-details-image">
        <img
          src={`http://localhost:8002/${blogDetails.image}`}
          alt={blogDetails?.title}
          className="portfolio-project-image"
        />
        <div className="code-copy">
          <pre>{blogDetails.decription}</pre>
        </div>
      </div>
    </div>
  );
};
export default BlogDetails;
