import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Users from './Users';
import Products from './Products';
import AddBlog from './AddBlog';
import '../assets/dashbord.css';

const Dashboard = () => {
  const [activeSection, setActiveSection] = useState('home');
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) return;

        const response = await fetch("http://localhost:8002/api/profile", {
          method: "GET",
          headers: { "Authorization": token, "Content-Type": "application/json" },
        });

        const data = await response.json();
        if (response.ok) {
          setUser(data.profile);
        }
      } catch (err) {
        console.error("Error fetching profile", err);
      }
    };

    fetchUserProfile();
  }, []);

  const handleProfileOption = (event) => {
    const selectedOption = event.target.value;
    if (selectedOption === 'view-profile') {
      navigate('/profile'); 
    } else if (selectedOption === 'logout') {
      localStorage.removeItem('token');
      navigate('/login');
    }
  };

  return (
    <div className="admin-panel">
      <header>
        <div className="logosec">
          <div className="logo">Admin Dashboard</div>
        </div>
        <div className="message">
          <div className="dp user-profile">
            <img 
              src={user?.profileImage 
                ? `http://localhost:8002${user.profileImage}`
                : "https://via.placeholder.com/120"
              }
              className="dpicn"
              alt="profile"
            />
            <select onChange={handleProfileOption}>
              <option value="">Options</option>
              <option value="view-profile">View Profile</option>
              <option value="logout">Logout</option>
            </select>
          </div>
        </div>
      </header>
    </div>
  );
};

export default Dashboard;
