import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const BlogDetails = () => {
  const [blogDetails, setBlogDetails] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:8002/api/get-blog/${id}`);
        if (!response.ok) throw new Error("Failed to fetch data");
        const result = await response.json();
        setBlogDetails(result);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, [id]);

  if (!blogDetails) return <p>Loading...</p>;

  return (
    <div className="main-blog-details">
      <h2>{blogDetails.title}</h2>
      <div className="portfolio-details-image">
        <img
          src={`http://localhost:8002/uploads/${blogDetails.image}`}
          alt={blogDetails.title}
          className="portfolio-project-image"
        />
        <div className="code-copy">
          <pre>{blogDetails.description}</pre>
        </div>
      </div>
    </div>
  );
};

export default BlogDetails;
