import './assets/resetpassword.css';
import React, { useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ResetPasswordPage = () => {
  const { token } = useParams(); 
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState(''); 

  const handlePasswordReset = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:8000/api/resetPassword/${token}`, { password });
      if (response.status === 200) {
        setSuccessMessage("Password reset successful");
        setPassword('');
      }
      setMessage(response.data.message || 'Password reset completed.');
    } catch (error) {
      setMessage(error.response?.data?.message || 'An error occurred while resetting the password.');
    }
  };

  return (
    <div className='reset-passord'>
      <h2>Reset Password</h2>
      {successMessage && <p classNameName="success-message">{successMessage}</p>}
      <form onSubmit={handlePasswordReset}>
        <input
        className='form-control inputBox'
          type="password"
          placeholder="New Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Reset Password</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default ResetPasswordPage;
