import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const BlogDetails = () => {
  const [blogDetails, setBlogDetails] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log(`Fetching blog with ID: ${id}`); // Debugging
        const response = await fetch(`http://localhost:8002/api/get-blog/${id}`);
        if (!response.ok) throw new Error("Failed to fetch data");
        const result = await response.json();
        console.log("Fetched Data:", result); // Debugging
        setBlogDetails(result);
      } catch (error) {
        console.error("Fetch error:", error);
      }
    };

    fetchData();
  }, [id]);

  if (!blogDetails) return <p>Loading...</p>;

  return (
    <div className="main-blog-details">
      <h2>{blogDetails.title}</h2>
      <h2>Testing</h2>
    </div>
  );
};

export default BlogDetails;
