import React, {useState ,useEffect} from "react";

const Blog = ()=>{
    const [blog ,setBlog] = useState([]);

    const showblog = async ()=>{
    const showBlogApi = await fetch(`http://localhost:8000/api/show-blog/`);
    const fetchData = await showBlogApi.json();
    setBlog(fetchData);
    }
    useEffect(() => {
        showblog();
      }, []);
   return(
    <div className="main-blogs">
     {blog.map((blogItem, i)=>(
     <div className="blog-item-inner">
<h2>{blogItem.title}</h2>

    </div>
     ))}

    </div>
   )
}
export default Blog;
