import './assets/resetpassword.css';
import React, { useState } from 'react';

const ResetPasswordPage = () => {
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [resetPasswordToken] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handlePasswordReset = async (e) => {
    e.preventDefault();
    setMessage(''); // Reset message before sending a new request
    try {
      const response = await fetch(`http://localhost:8000/api/resetPassword/${resetPasswordToken}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ password }), 
      });
      if (response.status === 200) { 
        setSuccessMessage("Password reset successful");
        setPassword('');
      } else {
        const errorData = await response.json();
        setMessage(errorData.message || 'An error occurred while resetting the password.');
      }
    } catch (error) {
      setMessage('An error occurred while resetting the password.');
    }
  };

  return (
    <div className='reset-password'>
      <h2>Reset Password</h2>
      {successMessage && <p className="success-message">{successMessage}</p>}
      {message && <p className="error-message">{message}</p>} {/* Display error message */}
      <form onSubmit={handlePasswordReset}>
        <input
          className='form-control inputBox'
          type="password"
          placeholder="New Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Reset Password</button>
      </form>
    </div>
  );
};

export default ResetPasswordPage;
