import './assets/signup.css';
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const SignUp = () => {
  const [name, setName] = useState("");
  const [username, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [gender, setGender] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const auth = localStorage.getItem("user");
    if (auth) {
      navigate("/");
    }
  }, [navigate]);

  const SignUpForm = async (e) => {
    e.preventDefault();

    // Validate inputs
    if (!name || !username || !email || !password || !gender) {
      setError("All fields are required.");
      return;
    }
    if (!/\S+@\S+\.\S+/.test(email)) {
      setError("Please enter a valid email.");
      return;
    }
    if (password.length < 6) {
      setError("Password must be at least 6 characters.");
      return;
    }

    setError(""); // Clear error before making API call

    try {
      let result = await fetch("http://localhost:8000/api/register", {
        method: "POST",
        body: JSON.stringify({ name, username, gender, email, password }),
        headers: {
          "Content-Type": "application/json",
        },
      });
      result = await result.json();

      if (result.ok) {
        localStorage.setItem("user", JSON.stringify(result));
        setSuccess(true);
        navigate("/login");
      } else {
        setError(result.message || "Registration failed. Please try again.");
      }
    } catch (err) {
      setError("An error occurred while registering. Please try again.");
    }
  };

  return (
    <div className="outer-signup-form">
      <div className="custom-sign-up">
        <h2>Sign Up</h2>
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">Registration successful! Redirecting to login...</div>}
        <form className="main-input-box" onSubmit={SignUpForm}>
          <div className="form-input">
            <input
              className="inputBox"
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Enter Name"
              required
            />
          </div>
          <div className="form-input">
            <input
              className="inputBox"
              type="text"
              value={username}
              onChange={(e) => setUserName(e.target.value)}
              placeholder="Enter Username"
            />
          </div>
          <div className="form-input">
            <input
              className="inputBox"
              type="text"
              value={gender}
              onChange={(e) => setGender(e.target.value)}
              placeholder="Enter Gender"
              required
            />
          </div>
          <div className="form-input">
            <input
              className="inputBox"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter Email"
              required
            />
          </div>
          <div className="form-input">
            <input
              className="inputBox"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter Password"
              required
            />
          </div>
          <button type="submit" className="Sign-up-btn btn">
            Sign Up
          </button>
        </form>
      </div>
    </div>
  );
};

export default SignUp;
