import React, { useState, useEffect } from "react";
import '../assets/users.css';

const Users = () => {
    const [users, setUsers] = useState([]);

    const fetchUsers = async () => {
        const response = await fetch(`http://localhost:8002/api/view-user`);
        const data = await response.json();
        setUsers(data.data);
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    const toggleVerification = async (userId, currentStatus) => {
        const newStatus = !currentStatus;

        const response = await fetch(`http://localhost:8002/api/update-verification/${userId}`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ verified: newStatus }),
        });

        if (response.ok) {
            setUsers(users.map(user => 
                user.id === userId ? { ...user, verified: newStatus } : user
            ));
        }
    };

    return (
        <div className="users-container">
            <h2 className="table-title">User List</h2>
            <table className="user-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Gender</th>
                        <th>Role</th>
                        <th>Verified</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.gender}</td>
                            <td>{user.role}</td>
                            <td>
                                <input
                                    type="checkbox"
                                    checked={user.verified}
                                    onChange={() => toggleVerification(user.id, user.verified)}
                                />
                                <span className={user.verified ? "verified" : "not-verified"}>
                                    {user.verified ? "Verified" : "Not Verified"}
                                </span>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Users;
