import './assets/contact.css';
import React, {useState} from "react";
const ContactUs = ()=>{
const [name, setName] = useState('');
const [email, setEmail] =useState('');
const [messages, setMessage] =useState('');

const ContactSubmitData = async()=>{
        let result = await fetch("http://localhost:8000/api/contact/add", {
         method: "POST",
         body: JSON.stringify({name,email,messages}),
         headers:{
         'Content-Type':'application/json'
         },
       });
       if(result.status === 200){
            console.log('success')
       }else{
            console.log('false')
       }
   }
return(
    <div className="contact-page">
      <section id="contact" class="contact">
  <div class="contact-container">
    <h2>Contact Us</h2>
    <p>If you have any questions or would like to get in touch, feel free to reach out to me!</p>
    <ul>
      <li>Email: <a href="mailto: sandeepptpss@gmail.com"> sandeepptpss@gmail.com</a></li>
      <li>Mobile Number: <a href="tel:+919914765754">9914765754</a></li>
    </ul>
    <div class="contact-form-container">
            <div class="contact-form">
                <div class="form-group">
                    <label for="name">Name</label>
                    <input type="text" id="name"  name='Name' value={name} onChange={(e) => setName(e.target.value)} placeholder="Your Name" required/>
                </div>
                <div class="form-group">
                    <label for="email">Email</label>
                    <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)}  placeholder="Your Email" required/>
                </div>
                <div class="form-group">
                    <label for="message">Message</label>
                    <textarea id="message" name="message" rows="5" value={messages} onChange={(e) => setMessage(e.target.value)} placeholder="Your Message" required></textarea>
                </div>
                <button type="submit" onClick={ContactSubmitData} class="submit-btn">Send Message</button>
            </div>
        </div>

  </div>
</section>
    </div>
)
}
export default ContactUs;
