import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
const Product = () => {
const [portfolio, setProduct] = useState([]);
const showProduct = async()=>{
  const portfolioApi = await fetch(`https://dummyjson.com/products`);
  const fetchData = await portfolioApi.json();
  setProduct(fetchData);
}


useEffect(() => {
  showProduct();
}, []);
  return (
    <div className="portfolio">
      <header>
        <h1 className="portfolio_title">My Product</h1>
        <p className="portfolio-desc">Welcome to my portfolio! I am a web developer specializing in Shopify Plus.</p>
      </header>
      <div className="projects">
        <h2>Projects</h2>
        <div className="project-list">
          {portfolio.map((ProductItem, i)=>(
              <div className="project project-inner" key={i}>
                <h3>{ProductItem?.title}</h3>
                <p className="portfolio-description">{ProductItem?.description}</p>
                <div className="btn-wrapper">
                </div>
              </div>
            ))}
        </div>
      </div>
      <footer>
        <h2>Contact Us</h2>
        <p>Email: info@tech-prastish.com</p>
      </footer>
    </div>
  );
};
export default Product;
