import React, { useState } from "react";
import { useParams } from "react-router-dom";

   const PortfolioDetails = () => {
  const [details, setDetails] = useState({});
  const [data, setData] = useState([]);
  console.log(data)
  const { id } = useParams();

  // Fetch details for the specific portfolio item
  const fetchData = async () => {
    const response = await fetch(`http://127.0.0.1:8000/api/detail/${id}`);
    console.log("response",response)
    const result = await response.json();
    setDetails(result);
};
  return (
    <>
      <div className="main-product-detail">
        <div className="main-inner-side">
          <div className="left-side-data">
            {details.image && (
              <img
                src={`http://localhost:8000/${details.image}`}
                width="500px"
                height="450px"
                alt={details.title || "Product"}
              />
            )}
          </div>
          <div className="right-side-data">
            <h2>{details.title}</h2>
          </div>
        </div>
        <p className="product-description">{details.description}</p>
      </div>
    </>
  );
};

export default PortfolioDetails;
