import React, { useState } from 'react';
import Users from './Users';
import Products from './Products';
import Blog from './Blog';
import '../assets/dashbord.css';


const Dashboard = () => {
  const [activeSection, setActiveSection] = useState('home');

  const renderContent = () => {
    switch (activeSection) {
      case 'users':
        return <Users />;
      case 'products':
        return <Products />;
        case 'blog':
        return <Blog />;
      default:
        return (
          <div className="main-content">
            <h2>Welcome to the Admin Panel</h2>
            <p>Select an option from the sidebar to get started.</p>
          </div>
        );
    }
  };

  return (
    <div className="admin-panel">
      <header>
        <div className="logosec">
          <div className="logo">Admin Dashboard</div>
        </div>
        <div className="message">
          <div className="circle"></div>
          <img 
            src="https://media.geeksforgeeks.org/wp-content/uploads/20221210183322/8.png"
            className="icn"
            alt="notifications"
          />
          <div className="dp">
            <img 
              src="https://themewagon.github.io/deskapp2/vendors/images/photo1.jpg"
              className="dpicn"
              alt="profile"
            />
          </div>
        </div>
      </header>

      {/* Sidebar Navigation */}
      <div className="navcontainer">
        <nav className="nav">
          <div className="nav-upper-options">
            <div 
              className={`nav-option ${activeSection === 'home' ? 'active' : ''}`} 
              onClick={() => setActiveSection('home')}
            >
              <img 
                src="https://media.geeksforgeeks.org/wp-content/uploads/20221210182148/Untitled-design-(29).png"
                className="nav-img"
                alt="dashboard"
              />
              <h3>Dashboard</h3>
            </div>

            <div 
              className={`nav-option ${activeSection === 'users' ? 'active' : ''}`} 
              onClick={() => setActiveSection('users')}
            >
              <img 
                src="https://media.geeksforgeeks.org/wp-content/uploads/20221210183323/10.png"
                className="nav-img"
                alt="user"
              />
              <h3>User</h3>
            </div>

            <div 
              className={`nav-option ${activeSection === 'addBlog' ? 'active' : ''}`} 
              onClick={() => setActiveSection('addBlog')}
            >
              <img 
                src="https://media.geeksforgeeks.org/wp-content/uploads/20221210183323/10.png"
                className="nav-img"
                alt="user"
              />
              <h3>Add Blog</h3>
            </div>
            <div 
              className={`nav-option ${activeSection === 'products' ? 'active' : ''}`} 
              onClick={() => setActiveSection('products')}
            >
              <img 
                src="https://cdn-icons-png.flaticon.com/512/2907/2907764.png"
                className="nav-img"
                alt="products"
              />
              <h3>Products</h3>
            </div>
          </div>
        </nav>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {renderContent()}
      </div>
    </div>
  );
};

export default Dashboard;
