import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const BlogDetails = () => {
  const [blog, setBlog] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log(`Fetching blog with ID: ${id}`); // Debugging
        const response = await fetch(`http://localhost:8002/api/get-blog/${id}`);
        if (!response.ok) throw new Error("Failed to fetch data");
        const result = await response.json();
        console.log("Fetched Data:", result); // Debugging
        setBlog(result);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [id]); // Add dependency array

  if (!blog) return <p>Loading...</p>;

  return (
    <div className="portfolio-blog">
      <h2>Portfolio Project Name: {blog.title}</h2>
      <div className="portfolio-blog-image">
        <img src={`http://localhost:8002/uploads/${blog.image}`} alt={blog.title} />
      </div>
      <p>{blog.description}</p>
    </div>
  );
};

export default BlogDetails;
