import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../custom.css";

const UserProfile = () => {
  const [user, setUser] = useState({
    name: "",
    email: "",
    username: "",
    gender: "",
    role: "",
    profile: ""
  });
  const [isEditing, setIsEditing] = useState(false);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          navigate("/login");
          return;
        }

        const response = await axios.get("http://localhost:5000/api/user/profile", {
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (response.data && response.data.viewProfile) {
          setUser(response.data.viewProfile);
        } else {
          setError("Invalid profile data received");
        }
      } catch (err) {
        console.error("Profile fetch error:", err);
        if (err.response) {
          if (err.response.status === 401 || err.response.status === 403) {
            localStorage.removeItem("token");
            navigate("/login");
          } else {
            setError(err.response.data?.message || "Failed to fetch profile");
          }
        } else {
          setError("Network error. Please try again later.");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      const response = await axios.put(
        "http://localhost:5000/api/user/profile",
        user,
        {
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data && response.data.message === "Profile updated successfully") {
        setMessage("Profile updated successfully");
        setIsEditing(false);
        // Refresh the profile data
        const profileResponse = await axios.get("http://localhost:5000/api/user/profile", {
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        if (profileResponse.data && profileResponse.data.viewProfile) {
          setUser(profileResponse.data.viewProfile);
        }
      }
    } catch (err) {
      console.error("Profile update error:", err);
      if (err.response) {
        setError(err.response.data?.message || "Failed to update profile");
      } else {
        setError("Network error. Please try again later.");
      }
    }
  };

  if (loading) return <div className="text-center mt-5">Loading...</div>;

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">
              <h3 className="text-center">User Profile</h3>
            </div>
            <div className="card-body">
              {message && (
                <div className="alert alert-success">{message}</div>
              )}
              {error && (
                <div className="alert alert-danger">{error}</div>
              )}

              {isEditing ? (
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label className="form-label">Name</label>
                    <input
                      type="text"
                      className="form-control"
                      name="name"
                      value={user.name}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Email</label>
                    <input
                      type="email"
                      className="form-control"
                      name="email"
                      value={user.email}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Username</label>
                    <input
                      type="text"
                      className="form-control"
                      name="username"
                      value={user.username}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Gender</label>
                    <select
                      className="form-control"
                      name="gender"
                      value={user.gender}
                      onChange={handleChange}
                      required
                    >
                      <option value="male">Male</option>
                      <option value="female">Female</option>
                      <option value="other">Other</option>
                    </select>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Role</label>
                    <select
                      className="form-control"
                      name="role"
                      value={user.role}
                      onChange={handleChange}
                      required
                    >
                      <option value="user">User</option>
                      <option value="admin">Admin</option>
                    </select>
                  </div>
                  <div className="d-grid gap-2">
                    <button type="submit" className="btn btn-primary">Save Changes</button>
                    <button
                      type="button"
                      className="btn btn-secondary"
                      onClick={() => setIsEditing(false)}
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              ) : (
                <div>
                  <div className="text-center mb-4">
                    <img
                      src={user.profile ? `http://localhost:5000/${user.profile}` : "https://via.placeholder.com/120"}
                      alt="Profile"
                      className="rounded-circle"
                      width={120}
                      height={120}
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Name</label>
                    <p className="form-control-static">{user.name}</p>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Email</label>
                    <p className="form-control-static">{user.email}</p>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Username</label>
                    <p className="form-control-static">{user.username}</p>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Gender</label>
                    <p className="form-control-static">{user.gender}</p>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Role</label>
                    <p className="form-control-static">{user.role}</p>
                  </div>
                  <div className="d-grid">
                    <button
                      className="btn btn-primary"
                      onClick={() => setIsEditing(true)}
                    >
                      Edit Profile
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
