import React, {useState } from "react";
const CreatePortfolio = ()=>{
    const [title, setTitle] =useState();
    const [description , setDescription] = useState();
    const [portfoliolink , setPortfoliolink] = useState();
    const [image, setImage] = useState(null); 
    const [successMessage, setSuccessMessage] = useState(''); 
    const submitPortfolio = async ()=>{
        const result =  await fetch("http://localhost:8000/api/add/portfolio", {
            method:'POST',
            body: JSON.stringify({ title,description,portfoliolink,image }),
            headers:{
             'Content-Type': 'application/json'
            }
        })
        console.log(result)
        if (result.ok) {
          setSuccessMessage("Your message has been sent successfully!");
          setTitle('');
          setDescription('');
          setPortfoliolink('');
          setImage('')
        }else{
       setSuccessMessage("There was an error sending your message. Please try again.");
        }
    }
    return(
        <div className="main-create-portfolio">
        <h2>Add PortForlio</h2> {successMessage &&
        <p classNameName="success-message">{successMessage}</p>}
        <div className="create-portfolio-form">
            <div className="form-group">
                <label htmlFor="title">Name</label>
                <input type="text" id="title" name='title' value={title} onChange={(e)=> setTitle(e.target.value)} placeholder="Your Title" required/>
            </div>
            <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea id="description" name="description" value={description} onChange={(e)=> setDescription(e.target.value)} rows="6">
                </textarea>
            </div>
            <div className="form-group">
                <label htmlFor="title">portfoliolink</label>
                <input type="text" id="portfoliolink" name='portfoliolink' value={portfoliolink} onChange={(e)=> setPortfoliolink(e.target.value)} placeholder="Portfolio link" required/>
            </div>
            <div className="form-group">
                <label htmlFor="image">Image</label>
                <input type="file" onChange={(e)=> setImage(e.target.files[0])} name="image" className="form-control"/>
            </div>
            <button type="submit" onClick={submitPortfolio} className="submit-btn">Send Message</button>
        </div>
    </div>
   )
}
export default CreatePortfolio;
