import './assets/resetpassword.css';
import React, { useState, useEffect } from 'react';

const ResetPasswordPage = () => {
  const [password, setPassword] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [resetPasswordToken, setResetPasswordToken] = useState('');

  // Use effect to extract token from URL query parameters
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const token = urlParams.get('token'); // Extract 'token' parameter from the URL
    if (token) {
      setResetPasswordToken(token); // Set the token value to state
    }
  }, []);

  const PasswordReset = async (e) => {
    e.preventDefault(); // Prevent form reload
    if (!resetPasswordToken) {
      setSuccessMessage("Reset token is missing.");
      return;
    }
    try {
      const result = await fetch(`http://localhost:8000/api/resetPassword/${resetPasswordToken}`, {
        method: "POST",
        body: JSON.stringify({ password, resetPasswordToken }),
        headers: {
          'Content-Type': 'application/json'
        },
      });

      if (result===200) {
        setSuccessMessage("Your password has been reset successfully!");
        setPassword('');
      } else if(result.status === 400) {
        setSuccessMessage("Invalid token or email is required.");
      }
    } catch (error) {
      console.error("Error:", error);
      setSuccessMessage("An error occurred. Please try again later.");
    }
  };

  return (
    <div className='reset-password'>
      <h2>Reset Password</h2>
      {successMessage && <p className="success-message">{successMessage}</p>}

      <form onSubmit={PasswordReset}> {/* Handle form submission here */}
        <input
          className='form-control inputBox'
          type="password"
          placeholder="New Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Reset Password</button>
      </form>
    </div>
  );
};

export default ResetPasswordPage;
