import '../assets/add-portfolio.css';
import React, {useState } from "react";
const CreatePortfolio = () => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [portfoliolink, setPortfoliolink] = useState('');
    const [image, setImage] = useState(null);
    const [successMessage, setSuccessMessage] = useState('');

    const submitPortfolio = async () => {
      const formData = new FormData();
      formData.append('title', title);
      formData.append('description', description);
      formData.append('portfoliolink', portfoliolink);
      if (image) formData.append('image', image);
      try {
        const response = await fetch("http://localhost:8000/api/add/portfolio", {
          method: 'POST',
          body: formData,
        });
        if (response.ok) {
          setSuccessMessage("Your message has been sent successfully!");
          setTitle('');
          setDescription('');
          setPortfoliolink('');
          setImage(null);
        } else {
          setSuccessMessage("There was an error sending your message. Please try again.");
        }
      } catch (error) {
        console.error("Error submitting portfolio", error);
        setSuccessMessage("There was an error sending your message. Please try again");
      }
    };
    return(

        <div className="main-create-portfolio">
        <h2>Add PortForlio</h2> 
        {successMessage && <p classNameName="success-message">{successMessage}</p>}
        <div className="create-portfolio-form">
            <div className="form-group">
                <label htmlFor="title">Name</label>
                <input type="text" id="title" name='title' value={title} onChange={(e)=> setTitle(e.target.value)} placeholder="Your Title" required/>
            </div>
            <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea id="description" name="description" value={description} onChange={(e)=> setDescription(e.target.value)} rows="6">
                </textarea>
            </div>
            <div className="form-group">
                <label htmlFor="portfoliolink">Portfoliolink</label>
                <input type="text" id="portfoliolink" name='portfoliolink' value={portfoliolink} onChange={(e)=> setPortfoliolink(e.target.value)} placeholder="Portfolio link" required/>
            </div>
            <div className="form-group">
                <label htmlFor="image">Feature Image</label>
                <input id="image" type="file" onChange={(e)=> setImage(e.target.files[0])} name="image" className="form-control"/>
            </div>
            <button type="submit" onClick={submitPortfolio} className="submit-btn">Send Message</button>
        </div>
    </div>
   )
}
export default CreatePortfolio;
