import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const PortfolioDetails = () => {
    const [details, setDetails] = useState(null); // Updated to handle a single object
    const { id } = useParams();

    const fetchData = async () => {
        try {
            const response = await fetch(`http://localhost:8000/api/getportfolio/${id}`);
            if (!response.ok) {
                throw new Error("Failed to fetch data");
            }
            const result = await response.json();
            setDetails(result);
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

    useEffect(() => {
        fetchData();
    }, [id]); // Adding `id` as a dependency for fetching when it changes

    if (!details) {
        return <p>Loading...</p>; // Loading state
    }

    return (
        <div className="portfolio-details">
            <div className="banner-image">
                <img 
                    src={details.imageUrl} // Assuming `imageUrl` is the field for the image
                    alt={details.title || "Portfolio image"} 
                    style={{ maxWidth: "100%", height: "auto" }} 
                />
            </div>
            <h1>{details.title}</h1> {/* Assuming `title` is the field for the title */}
            <p>{details.description}</p> {/* Assuming `description` is the field for the description */}
        </div>
    );
};

export default PortfolioDetails;
