import './assets/portfolio-details.css';
import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
const PortfolioDetails = () => {
    const [details, setDetails] = useState(null);
    const { id } = useParams();
    const fetchData = async()=>{
            const response = await fetch(`http://localhost:8000/api/getportfolio/${id}`);
            if (!response.ok) {
                throw new Error("Failed to fetch data");
            }
            const result = await response.json();
            setDetails(result);
    };
    if (!details) {
        return <p>Loading...</p>;
    }
    return (
        <div className="portfolio-details">
            <h2>Portfolio Project Deatils</h2>
            <div className="portfolio-details-image">
            <img src={`http://localhost:8000/${details.image}`} alt={details?.title} className="portfolio-project-image"/>
            </div>
            <h1>{details.title}</h1>
            <p>{details.description}</p>
        </div>
    );
};
export default PortfolioDetails;
