import '../assets/product.css'
import React , {useState ,useEffect} from "react";
const Product= ()=>{
const [product, setProduct] =useState([]);
const showProduct =async ()=>{
const featchProduct = await fetch(`https://dummyjson.com/products`);
const featchData = await featchProduct.json();
setProduct(featchData.products);
    }
    useEffect(()=>{
      showProduct();
    },[]);
return(
    <div className="main-product">
      {product.map((productItem, i)=>(
      <div className="product-inner" key={i}>
      <div className='product-images'>
      <img
        src={productItem.images[0]} alt={productItem.title} className="product-image" width='300px'/>
      </div>
        <div className="product-price-with-title">
        <h2>{productItem.title}</h2>
      <p className="price">${productItem.price}</p>
      </div>
    </div>
  ))}
</div>
);
};
export default Product;
