import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          console.error("No token found, redirecting to login");
          navigate("/login");
          return;
        }

        const response = await fetch("http://localhost:8002/api/view-user-profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const text = await response.text(); // Read response as text first
        console.log("üì¢ Raw API Response:", text);

        try {
          const data = JSON.parse(text); // Try parsing JSON
          console.log("‚úÖ Parsed JSON Response:", data);

          if (!response.ok) {
            if ([401, 403].includes(response.status)) {
              localStorage.removeItem("token");
              navigate("/login");
            }
            throw new Error(data.message || "Failed to fetch profile");
          }

          setUser(data); // ‚úÖ No need to extract `viewProfile`
        } catch (jsonError) {
          throw new Error("‚ùå Backend did not return valid JSON");
        }
      } catch (err) {
        console.error("‚ùå Error fetching profile:", err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [navigate]);

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h2>User Profile</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {user ? (
        <div>
          <img
            src={user.profileImage || "https://via.placeholder.com/120"}
            alt="Profile"
            width={120}
            height={120}
          />
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Username:</strong> {user.username}</p>
          <p><strong>Gender:</strong> {user.gender || "Not specified"}</p>
        </div>
      ) : (
        !error && <p>No profile data found</p>
      )}
    </div>
  );
};

export default UserProfile;
