import '../assets/login.css';
import React, { useState, useEffect } from "react";
import { Link, useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [successMessage, setSuccessMessage] = useState(''); 
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  // Check authentication and role on initial render
  useEffect(() => {
    const user = localStorage.getItem('user');
    if (user) {
      const { role } = JSON.parse(user);
      if (role === 'admin') {
        navigate('/admin/dashbord');
      } else {
        navigate('/home'); // Redirect non-admin users to home
      }
    }
  }, [navigate]);

  // Handle login
  const loginUser = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/auth/login', {
        method: 'POST',
        body: JSON.stringify({ email, password }),
        headers: {
          "Content-Type": "application/json"
        }
      });

      if (response.ok) {
        const data = await response.json();
        const { role } = data; // Extract the role from the response

        // Store user data in localStorage
        localStorage.setItem('user', JSON.stringify(data));

        // Display success message
        setSuccessMessage("Login successfully!");
        setEmail('');
        setPassword('');

        // Redirect based on role
        if (role === 'admin') {
          navigate('/admin/dashbord');
        } else {
          navigate('/'); // Redirect non-admin users
        }
      } else {
        const errorData = await response.json();
        setErrorMessage(errorData.message || 'Incorrect email or password');
      }
    } catch (error) {
      setErrorMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div className="main-login-page">
      <div className="login-page">
        <h2>Login</h2>
        {successMessage && <p className="success-message">{successMessage}</p>}
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <div className="main-login-input-page">
          <input 
            type="email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            className="form-control inputBox" 
            placeholder="Enter email or username" 
          />
          <input 
            type="password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            className="form-control inputBox" 
            placeholder="Enter Password" 
          />
        </div>
        <button 
          type="submit" 
          onClick={loginUser} 
          className="btn btn-primary"
        >
          Login
        </button>
        <Link to="/forgot-password">Forgot your password?</Link>
      </div>
    </div>
  );
};

export default Login;
