import React, { useState, useRef } from "react";
import { Link, useNavigate } from 'react-router-dom';
import ReCAPTCHA from "react-google-recaptcha";
import toast, { Toaster } from 'react-hot-toast';
import '../assets/login.css';
const Login = () => {
  const recaptcha = useRef(null);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();
  const loginUser = async () => {
    console.log(import.meta.env ,'hello ');

    if(!recaptcha.current.getValue()){
      toast.error('Please Submit Captcha')
    }
    try {

      const result = await fetch('http://localhost:8000/api/auth/login', {
        method: 'POST',
        body: JSON.stringify({ email, password }),
        headers: {
          "Content-Type": "application/json"
        }
      });
      if (result.ok) {
        const data = await result.json();
        localStorage.setItem('user', JSON.stringify(data));
        setSuccessMessage("Login successfully!");
        setEmail('');
        setPassword('');
        const { role } = data.user; 
        if (role === 'admin') {
          navigate('/admin/dashbord');
        } else {
          navigate('/');
        }
      } else {
        const errorData = await result.json();
        setErrorMessage(errorData.message || 'Incorrect email or password');
      }
    } catch (error) {
      setErrorMessage('An error occurred. Please try again.');
    }
  };
  return (
    <div className="main-login-page">
      <div className="login-page">
        <h2>Login</h2>
        {successMessage && <p className="success-message">{successMessage}</p>}
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <div className="main-login-input-page">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="form-control inputBox"
            placeholder="Enter email or username"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="form-control inputBox"
            placeholder="Enter Password"
          />
        </div>
        <button
          type="submit"
          onClick={loginUser}
          className="btn btn-primary"
        >
          Login
        </button>
        <Toaster/>

        <Link to="/forgot-password">Forgot your password?</Link>
      </div>
    </div>
  );
};
export default Login;
