import React, {useState ,useEffect} from "react";

const Blog = ()=>{
    const [blog ,setBlog] = useState([]);
    const showblog = async () => {
        try {
            const showBlogApi = await fetch(`http://localhost:8000/api/show-blog/`);
            if (!showBlogApi.ok) {
                throw new Error(`Error: ${showBlogApi.status}`);
            }
            const fetchData = await showBlogApi.json();
            setBlog(fetchData.blogs);
        } catch (error) {
            console.error("Failed to fetch blogs:", error);
        }
    };
    useEffect(() => {
        showblog();
