import React, { useState } from "react";
import { Link } from 'react-router-dom';
import './assets/nav.css'; // Import the CSS file


const Nav = () => {
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

    const toggleMobileMenu = () => {
      setIsMobileMenuOpen(!isMobileMenuOpen);
    };

    const closeMobileMenu = () => {
      setIsMobileMenuOpen(false);
    };

    return (
      <div className="main-menu">
        <div className="logo">My Logo</div>

        {/* Hamburger Menu (Visible only on mobile) */}
        <div className={`hamburger ${isMobileMenuOpen ? 'open' : ''}`} onClick={toggleMobileMenu}>
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>
        </div>

        {/* Drawer Menu (Visible only on mobile view) */}
        <div className={`drawer-menu ${isMobileMenuOpen ? 'open' : ''}`}>
          <ul>
            <li><Link to="/" onClick={closeMobileMenu}>Portfolio</Link></li>
            <li><Link to="/about" onClick={closeMobileMenu}>About</Link></li>
            <li><Link to="/contact" onClick={closeMobileMenu}>Contact Us</Link></li>
          </ul>
        </div>

        {/* Desktop Navigation Menu (Visible on desktop view) */}
        <div className="desktop-nav">
          <ul>
            <li><Link to="/about">About</Link></li>
            <li><Link to="/contact">Contact Us</Link></li>
            <li><Link to="/portfolio">Portfolio</Link></li>
          </ul>
        </div>
      </div>
    );
  };

  export default Nav;
