import '../assets/blog.css';
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
const Blog = () => {
    const [blogs, setBlogs] = useState([]);
    const slugify = (text) => text.toLowerCase().replace(/\s+/g, '-').replace(/[^\w-]+/g, '');

    const showblog = async () => {
        const responseApi = await fetch(`http://localhost:8000/api/get-all-blog`);
        const fetchData = await responseApi.json();
        setBlogs(fetchData.data);
    };
    useEffect(() => {
        showblog();
    },[]);
    return (
        <div className="main-blogs">
            {blogs.map((blogItem, i)=>{
                const createdAt = new Date(blogItem.createdAt);
                const isValidDate = !isNaN(createdAt.getTime());
                const formattedDate = isValidDate
                    ? createdAt.toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' })
                    : '';
                return (
                    <div className="blog-item-inner" key={i}>
                        <h2>{blogItem.title}</h2>
                        {<p>{formattedDate}</p>}
                        <img 
                            src={`http://localhost:8000/${blogItem.image}`} 
                            alt={blogItem?.title} 
                            className="blogs-image" 
                        />
                        {/* <div className="blog-description">
                            <h2>Liquid Code Shopify</h2>
                            {blogItem.content.slice(0, 2)}â€¦
                        </div> */}
                        <Link className="live-demo-link" to={`/blog-details/${blogItem.title}`} rel="noopener noreferrer">
                    Get Source Code
                    </Link> 
                    <Link className="live-demo-link" to={`/blog-details/${slugify(blogItem.title)}`} rel="noopener noreferrer">
    Get Source Code
</Link>

                    </div>
                );
            })}
        </div>
    );
};
export default Blog;
