import { BrowserRouter, Routes, Route, useLocation } from 'react-router-dom';
import Nav from '../components/Nav';
import NoPage from '../pages/NoPage';
import Dashboard from '../admin/Dashboard';
import SignUp from '../components/SignUp';
import Login from '../components/Login';
import ResetPassword from '../components/ResetPasswordPage';
import ForgotPasswordPage from '../components/ForgotPassword';
import Footer from '../components/Footer';
import ProtectedRoute from '../components/ProtectedRoute';
import Home from '../pages/Home';
// Component to conditionally render Nav based on current route

const FooterLayout = () => {
  const location = useLocation();
  const isDashboardRoute = location.pathname.startsWith('/admin/dashbord');
  return(
    <>
    {!isDashboardRoute && <Footer />}
    </>
  );
};
const Router = () =>{
  return (
    <BrowserRouter>
      <Nav />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/signup" element={<SignUp />} />
        <Route path="/login" element={<Login />} />
        <Route path="/forgot-password" element={<ForgotPasswordPage />} />
        <Route path="/reset-password" element={<ResetPassword />} />
        <Route path="*" element={<NoPage />} />
        <Route path="/admin/dashbord" element={<ProtectedRoute allowedRoles={['admin']}><Dashboard /></ProtectedRoute>} />
      </Routes>
      <FooterLayout />
    </BrowserRouter>
  );
};
export default Router;
