import '../assets/signup.css';
import React, {useState ,Link  } from "react";
import {useNavigate } from "react-router-dom";

const SignUp = () => {
  const [name, setName] = useState("");
  const [username, setUser] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [gender, setGender] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();
  // useEffect(()=>{
  //     const auth =localStorage.getItem('user');
  //     if(auth){
  //         navigate('/')
  //     }
  // })
  const SignUpForm = async (e) => {
    e.preventDefault(); // Prevent form submission from reloading the page

    if (!username) {
      setError("Username are required.");
      return;
    }
    if (!gender) {
      setError("Gender are required.");
      return;
    }
    if (!/\S+@\S+\.\S+/.test(email)) {
      setError("Please enter a valid email.");
      return;
    }
    if (password.length < 6) {
      setError("Password must be at least 6 characters.");
      return;
    }
    setError(""); 
    console.warn(name, email, password);
    let result = await fetch("http://localhost:8002/api/register", {
      method: "POST",
      body: JSON.stringify({ name, username, gender, email, password }),
      headers: {
        'Content-Type': 'application/json',
      },
    });
    result = await result.json();
    if (result) {
      setSuccess(true);
      localStorage.setItem("user", JSON.stringify(result));
      setTimeout(()=>{
      navigate('/login');
      },600)
    }
  };
  return (
    <div className='outer-signup-form'>
    <div className="custom-sign-up">
    <h2>Sign Up</h2>
    {error && <div className="error-message">{error}</div>}
    {success && <div className="success-message">Registration successful! Redirecting to login...</div>}
      <form className="main-input-box" onSubmit={SignUpForm}>
      <div className='form-input'>
        <input
          className="inputBox"
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter Name"
        />
        </div>
        <div className='form-input'>
        <input
          className="inputBox"
          type="text"
          value={username}
          onChange={(e) => setUser(e.target.value)}
          placeholder="Enter Username"
        />
        </div>
        <div className='form-input'>
      <input
          className="inputBox"
          type="text"
          value={gender}
          onChange={(e) => setGender(e.target.value)}
          placeholder="Enter Gender"
        />
        </div>
        <div className='form-input'>
        <input
          className="inputBox"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter Email"
          required
        />
        </div>
        <div className='form-input'>
        <input
          className="inputBox"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter Password"
          required
        />
        </div>
        <button type="submit" onClick={SignUpForm} className="Sign-up-btn brn">Sign Up</button>

        <Link to="/login">login</Link>
      </form>
    </div>
    </div>
  );
};
export default SignUp;
