import React, { useState,useNavigate } from "react";
import { Link } from 'react-router-dom';

const Nav = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate= useNavigate();
  const auth =localStorage.getItem('user');

  // Toggle the menu open/close state
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);

  };
  const logout=()=>{
    localStorage.clear();
        navigate('/signup')
     } 

  return (
    <div className="main-menu">
      <div className="hamburger" onClick={toggleMenu}>
        &#9776;
      </div>

      <ul className={`menu-items ${isMenuOpen ? "open" : ""}`}>
        <li onClick={toggleMenu}><Link to="/">Portfolio</Link></li>
        <li onClick={toggleMenu}><Link to="/about">About</Link></li>
        <li onClick={toggleMenu}><Link to="/contact">Contact Us</Link></li>

        {/* Close Button (X) */}
        <li className="close-btn" onClick={toggleMenu}>
          &#10005; {/* X icon */}
        </li>

        {/* Mobile Signup and Login Buttons */}
        <li className="hide" onClick={toggleMenu}>
          <Link to="/signup">Signup</Link>
        </li>
        <li onClick={toggleMenu}>
          <Link className="hide" to="/login">Login</Link>
        </li>
      </ul>
      <ul>
      <li onClick={toggleMenu}>
          <Link to="/signup">Signup</Link>
        </li>
        <li onClick={toggleMenu}>
          <Link to="/login">Login</Link>
        </li>
        </ul>
    </div>
  );
};

export default Nav;
