import React, { useState, useEffect } from "react";
import '../assets/users.css';

const Users = () => {
    const [User, setUser] = useState([]);

    const showUser = async () => {
        const responseApi = await fetch(`http://localhost:8002/api/view-user`);
        const fetchData = await responseApi.json();
        setUser(fetchData.data);
    };

    useEffect(() => {
        showUser();
    }, []);

    return (
        <div className="users-container">
            <h2 className="table-title">User List</h2>
            <table className="user-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Gender</th>
                        <th>Role</th>
                        <th>Verified</th>
                    </tr>
                </thead>
                <tbody>
                    {User.map((user) => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.gender}</td>
                            <td>{user.role}</td>
                            <td className={user.verified ? "verified" : "not-verified"}>
                                {user.verified ? "Verified" : "Not Verified"}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Users;
