import React, {useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Header from "./Header";
const PortfolioDetails = () => {
  const [details, setDetails] = useState([]);
  const [data, setData] = useState([]);
  console.log(data)
const {id}=useParams()
  const fetchData = async () => {
      const response = await fetch(`http://localhost:8000/api/getportfolio/${id}`);
      console.log("response",response)
      const result = await response.json();
      setDetails(result);
  };
  useEffect(() => {
    fetchData();
  }, []);
  const fetchData1 = async() => {
      const response = await fetch(`http://localhost:8000/api/portfolioview/`);
      const result = await response.json();
      setData(result);
    };
  useEffect(() => {
    fetchData1();
  }, []);
  return (
    <>
      <Header />
      <div className="main-product-detail">
        <h2>Product Page</h2>
        <div className="main-inner-side">
          <div className="left-side-data">
            <img src={`http://localhost:8000/${details.image}`} width="500px" height="450px" alt="Product" />
          </div>
          <div className="right-side-data">
            <h2>{details.title}</h2>
          </div>
        </div>
        <p className="product-description">{details.description}</p>
      </div>
    </>
  );
};
export default PortfolioDetails;
