import './assets/signup.css';
import React, {useState  } from "react";
const Register = () => {
  const [username, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");

  const SignUpForm = async (e) => {
    e.preventDefault(); // Prevent form submission from reloading the page
    console.warn(phone, phone.length, email, password);
    let result = await fetch("https://cc1d-122-180-85-113.ngrok-free.app/api/register", {
      method: "POST",
      body: JSON.stringify({ username, email,  phone ,password }),
      headers: {
        'Content-Type': 'application/json',
      },
    });
    result = await result.json();
    localStorage.setItem('user', JSON.stringify(result));

    if (result.ok) {
        alert('success');
    }else{
    console.log(result.message);
    }
  };
  return (
    <div className='outer-signup-form'>
    <div className="custom-sign-up">
    <h2>Sign Up</h2>
      <form className="main-input-box" onSubmit={SignUpForm}>

        <div className='form-input'>
        <input
          className="inputBox"
          type="text"
          value={username}
          onChange={(e) => setUserName(e.target.value)}
          placeholder="Enter Username"
        />
        </div>
        <div className='form-input'>
       <input
          className="inputBox"
          type="text"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          placeholder="Enter Phone"
        />
        </div>
        <div className='form-input'>
        <input
          className="inputBox"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter Email"
          required
        />
        </div>
        <div className='form-input'>
        <input
          className="inputBox"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter Password"
          required
        />
        </div>
        <button type="submit" onClick={SignUpForm} className="Sign-up-btn brn">Sign Up</button>
      </form>
    </div>
    </div>
  );
};
export default Register;
