import React, { useState } from 'react';

      const ResetPasswordPage = () => {
  const [password, setPassword] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [resetPasswordToken, setResetPasswordToken] = useState('');


  const PasswordReset = async () => {
    try {
        const result = await fetch("http://localhost:8000/api/contact/add", {
            method: "POST",
            body: JSON.stringify({ password, resetPasswordToken }),
            headers: {
                'Content-Type': 'application/json'
            },
        });
        if (result.ok) {
            setSuccessMessage("Your Reset Password successfully!");
            setPassword(''); 
            setMessage('');
        } else if(result.status === 400) {
            setSuccessMessage("Email is required.");
        }else{
       setSuccessMessage("There was an error sending your message. Please try again.");
        }
    } catch (error) {
        console.error("Error:", error);
        setSuccessMessage("An error occurred. Please try again later.");
    }
};




  const handlePasswordReset = async (e) => {
    e.preventDefault();
    setMessage(''); // Reset message before sending a new request
    if (!resetPasswordToken) {
      setMessage('Reset password token is missing.');
      return;
    }

    try {
      const response = await fetch(`http://localhost:8000/api/resetPassword/${resetPasswordToken}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ password }), 
      });
console.log(response , 'hello response')
      // Log the response content, not the token
      const responseData = await response.json();
      console.log(responseData);

      if (response.status === 200) { 
        setSuccessMessage("Password reset successful");
        setPassword('');
      } else {
        setMessage(responseData.message || 'An error occurred while resetting the password.');
      }
    } catch (error) {
      setMessage('An error occurred while resetting the password.');
    }
  };

  return (
    <div className='reset-password'>
      <h2>Reset Password</h2>
      {successMessage && <p className="success-message">{successMessage}</p>}
      {message && <p className="error-message">{message}</p>}

      <form onSubmit={PasswordReset}> {/* Use onSubmit for the form */}
        <input
          className='form-control inputBox'
          type="password"
          placeholder="New Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Reset Password</button> {/* Remove onClick here */}
      </form>
    </div>
  );
};

export default ResetPasswordPage;
