import React, { useState, useEffect } from "react";

const Portfolio = () => {
  const [portfolio, setPortfolio] = useState([]);

  const showPortfolio = async () => {
    try {
      const portfolioApi = await fetch(`http://localhost:8000/api/portfolioview/`);
      const fetchData = await portfolioApi.json();
      console.log(fetchData); // Log the response to check its structure
      setPortfolio(fetchData);
    } catch (error) {
      console.error("Error fetching portfolio data:", error);
    }
  };

  useEffect(() => {
    showPortfolio();
  }, []);

  return (
    <div className="portfolio">
      <header>
        <h1>My Portfolio</h1>
        <p>Welcome to my portfolio! I am a web developer specializing in Shopify Plus.</p>
      </header>
      <section className="projects">
        <h2>Projects</h2>
        <div className="project-list">
          {portfolio.map((portfolioItem) => (
            <div className="project" key={portfolioItem.id}>
              <img
                src={`http://localhost:8000/${portfolioItem.image}`}
                alt={portfolioItem?.title}
                className="project-image"
                onError={(e) => e.target.src = 'default_image.jpg'} // Fallback image
              />
              <h3>{portfolioItem.title}</h3>
              <p>{portfolioItem?.description}</p>
              <a href={portfolioItem?.portfoliolink} target="_blank" rel="noopener noreferrer">
                View Project
              </a>
            </div>
          ))}
        </div>
      </section>
      <footer>
        <h2>Contact Me</h2>
        <p>Email: sandeepptpss@gmail.com</p>
      </footer>
    </div>
  );
};

export default Portfolio;
