import './assets/resetpassword.css';
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const ResetPasswordPage = () => {
  const { token } = useParams();
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handlePasswordReset = async (e) => {
    e.preventDefault();

    try {
      // Sending the password in the request body as JSON
      const response = await fetch(`http://localhost:8000/api/resetPassword/${token}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ password }), // Correct way to send the password in JSON format
      });

      if (response.ok) {
        const data = await response.json(); // Parse the response JSON
        setSuccessMessage("Password reset successful");
        setPassword('');
      } else {
        const errorData = await response.json();
        setMessage(errorData.message || 'An error occurred while resetting the password.');
      }
    } catch (error) {
      setMessage('An error occurred while resetting the password.');
    }
  };

  return (
    <div className='reset-password'>
      <h2>Reset Password</h2>
      {successMessage && <p className="success-message">{successMessage}</p>}
      <form onSubmit={handlePasswordReset}>
        <input
          className='form-control inputBox'
          type="password"
          placeholder="New Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Reset Password</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default ResetPasswordPage;
