import React, { useEffect, useState } from "react";

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const userData = JSON.parse(localStorage.getItem("user")); // Assuming user data is stored in localStorage
        if (!userData || !userData._id) {
          setError("User not found. Please log in.");
          return;
        }
        const response = await fetch(`http://localhost:8002/api/profile/${userData._id}`);
        const data = await response.json();
        if (response.ok) {
          setUser(data.profile);
        } else {
          setError(data.message || "Failed to load profile.");
        }
      } catch (err) {
        setError("Error fetching profile.");
      }
    };

    fetchUserProfile();
  }, []);

  return (
    <div className="profile-container">
      <h2>User Profile</h2>
      {error && <p className="error-message">{error}</p>}
      {user ? (
        <div className="profile-card">
          {user.profileImage && (
            <img src={`http://localhost:8002/uploads/${user.profileImage}`} alt="Profile" className="profile-image" />
          )}
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Username:</strong> {user.username}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Gender:</strong> {user.gender}</p>
        </div>
      ) : (
        !error && <p>Loading profile...</p>
      )}
    </div>
  );
};

export default UserProfile;
