import './assets/login.css';
import React, { useState ,useEffect} from "react";
import { Link, useNavigate } from 'react-router-dom';

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [successMessage, setSuccessMessage] = useState('');
    const navigate = useNavigate();

    useEffect(()=>{
        const auth = localStorage.getItem('user');
        if(auth){
            navigate('/');
        }
    }, [navigate]);

    const loginUser = async () => {
        let result = await fetch('http://localhost:8000/api/auth/login', {
            method: 'POST',
            body: JSON.stringify({ email, password }),
            headers: {
                "Content-Type": "application/json"
            }
        });

        result = await result.json();

        if (result.name) {
            localStorage.setItem('user', JSON.stringify(result));
            setSuccessMessage("Login successful!");
            setEmail('');  // Clear email only after successful login
            setPassword(''); // Clear password after login
            navigate('/');   // Redirect to the home page
        } else {
            setSuccessMessage("Please enter correct details");
        }
    }

    return (
        <>
            <div className="main-login-page">
                <div className="login-page ">
                    <h2>Login</h2>
                    <br />
                    {successMessage && <p className="success-message">{successMessage}</p>}
                    <div className="main-login-input-page">
                        <input 
                            type="email" 
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                            className="form-control inputBox" 
                            placeholder="Enter email or User name" 
                        />
                        <br />
                        <input 
                            type="password" 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            className="form-control inputBox" 
                            placeholder="Enter Password" 
                        />
                        <br />
                    </div>
                    <button type="submit" onClick={loginUser} className="btn btn-primary">Login</button>
                    <br />
                    <Link to="/forgot-password">Forgot Password</Link>
                </div>
            </div>
        </>
    )
}

export default Login;
