import './assets/contact.css';
import ReCAPTCHA from "react-google-recaptcha";
import toast, { Toaster } from 'react-hot-toast';

import React, { useRef,useState} from "react";
const ContactUs = ()=>{
const recaptcha = useRef(null);
const [name, setName] = useState('');
const [email, setEmail] =useState('');
const [messages, setMessage] =useState('');
const [successMessage, setSuccessMessage] = useState(''); 
const ContactSubmitData = async () => {
    try {
        if(!recaptcha.current.getValue()){
            toast.error('Please Submit Captcha')
          }
        const result = await fetch("http://localhost:8000/api/contact/add", {
            method: "POST",
            body: JSON.stringify({ name, email, messages }),
            headers: {
                'Content-Type': 'application/json'
            },
        });

        if (result.ok) {
            setSuccessMessage("Your message has been sent successfully!"); // Update success message
            setName(''); 
            setEmail('');
            setMessage('');
        } else {
            setSuccessMessage("There was an error sending your message. Please try again.");
        }
    } catch (error) {
        console.error("Error:", error);
        setSuccessMessage("An error occurred. Please try again later.");
    }
};
return(
    <div className="contact-page">
      <section id="contact" class="contact">
  <div class="contact-container">
    <h2>Contact Us</h2>
    <p>If you have any questions or would like to get in touch, feel free to reach out to me!</p>
    <ul>
      <li>Email: <a href="mailto: sandeepptpss@gmail.com"> sandeepptpss@gmail.com</a></li>
      <li>Mobile Number: <a href="tel:+919914765754">9914765754</a></li>
    </ul>
    <div class="contact-form-container">
    {successMessage && <p className="success-message">{successMessage}</p>} {/* Display success message */}

            <div class="contact-form">
                <div class="form-group">
                    <label for="name">Name</label>
                    <input type="text" id="name"  name='Name' value={name} onChange={(e) => setName(e.target.value)} placeholder="Your Name" required/>
                </div>
                <div class="form-group">
                    <label for="email">Email</label>
                    <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)}  placeholder="Your Email" required/>
                </div>
                <div class="form-group">
                    <label for="message">Message</label>
                    <textarea id="message" name="message" rows="5" value={messages} onChange={(e) => setMessage(e.target.value)} placeholder="Your Message" required></textarea>
                </div>
                <button type="submit" onClick={ContactSubmitData} class="submit-btn">Send Message</button>
                <ReCAPTCHA sitekey={import.meta.env.LfF6owqAAAAANsrSQapUgJacXGBIpEDwsSGmtfZ} ref={recaptcha}/>

            </div>
            <Toaster/>
        </div>
  </div>
</section>
    </div>
)
}
export default ContactUs;
