import React, { useState, useEffect } from "react";
import '../assets/users.css';

const Users = () => {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await fetch(`http://localhost:8002/api/view-user`);
            const data = await response.json();
            setUsers(data.data);
        } catch (error) {
            console.error("Error fetching users:", error);
        }
    };
    const toggleVerification = async (userId, currentStatus) => {
        try {
            const newStatus = !currentStatus;

            const response = await fetch(`http://localhost:8002/api/update-verification/${userId}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ verified: newStatus }),
            });

            if (response.ok) {
                setUsers(prevUsers => 
                    prevUsers.map(user =>
                        user._id === userId ? { ...user, verified: newStatus } : user
                    )
                );
            } else {
                console.error("Failed to update user verification status");
            }
        } catch (error) {
            console.error("Error updating user", error);
        }
    }
    return (
        <div className="users-container">
            <h2 className="table-title">User List</h2>
            <table className="user-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Gender</th>
                        <th>Role</th>
                        <th>Verified</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user._id}>
                            <td>{user._id}</td>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.gender}</td>
                            <td className={user.role}>{user.role}</td>
                            <td>
                                <input
                                    type="checkbox"
                                    checked={user.verified}
                                    onChange={() => toggleVerification(user._id, user.verified)}
                                />
                                <span className={user.verified ? "verified" : "not-verified"}>
                                    {user.verified ? "Verified" : "Not Verified"}
                                </span>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};
export default Users;
