import React, { useState, useEffect } from "react";
const Product = () => {
  const [portfolio, setPortfolio] = useState([]); // Renamed to match convention

  const showProduct = async () => {
    try {
      const portfolioApi = await fetch(`https://dummyjson.com/products`);
      const fetchData = await portfolioApi.json();
      setPortfolio(fetchData.products); 
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  useEffect(() => {
    showProduct();
  }, []);

  return (
    <div className="portfolio">
      <div className="projects">
        <h2>Product Details</h2>
        <div className="project-list">
          {portfolio.map((productItem, i) => (
            <div className="project project-inner">
              <h3>{productItem?.title}</h3>
              <p className="portfolio-description">{productItem?.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};
export default Product;
