import './assets/blog.css';
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const Blog = () => {
    const [blogs, setBlogs] = useState([]);

    const showblog = async () => {
        const responseApi = await fetch(`http://localhost:8000/api/show-blog`);
        const fetchData = await responseApi.json();
        setBlogs(fetchData.data);
    };
    useEffect(() => {
        showblog();
    }, []);

    return (
        <div className="main-blogs">
            {blogs.map((blogItem, i) => {
                // Format the date here
                const createdAt = new Date(blogItem.createdAt);
                const formattedDate = createdAt.toLocaleDateString('en-US', { year: 'numeric', month: 'short', day: 'numeric' });
                return (
                    <div className="blog-item-inner" key={i}>
                        <h2>{blogItem.title}</h2>
                        <p>{formattedDate}</p> {/* Display formatted date */}
                        <img 
                            src={`http://localhost:8000/${blogItem.image}`} 
                            alt={blogItem?.title} 
                            className="blogs-image" 
                        />
                        <div className="blog-description">
                            <h2>Liquid Code Shopify</h2>
                            {blogItem.content.slice(0, 900)}â€¦
                        </div>
                        <Link className="live-demo-link" target="_blank" href="#">
                            View More
                        </Link>
                    </div>
                );
            })}
        </div>
    );
};

export default Blog;
