import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const Product = () => {
  const [portfolio, setPortfolio] = useState([]); // Renamed to match convention

  const showProduct = async () => {
    try {
      const portfolioApi = await fetch(`https://dummyjson.com/products`);
      const fetchData = await portfolioApi.json();
      setPortfolio(fetchData.products); // Accessing the `products` array from the API response
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  useEffect(() => {
    showProduct();
  }, []);

  return (
    <div className="portfolio">
      <header>
        <h1 className="portfolio_title">My Product</h1>
        <p className="portfolio-desc">
          Welcome to my portfolio! I am a web developer specializing in Shopify Plus.
        </p>
      </header>
      <div className="projects">
        <h2>Projects</h2>
        <div className="project-list">
          {portfolio.map((productItem, i) => (
            <div className="project project-inner" key={i}>
              <h3>{productItem?.title}</h3>
              <p className="portfolio-description">{productItem?.description}</p>
              <div className="btn-wrapper">
                {/* Add buttons or links if needed */}
              </div>
            </div>
          ))}
        </div>
      </div>
      <footer>
        <h2>Contact Us</h2>
        <p>Email: info@tech-prastish.com</p>
      </footer>
    </div>
  );
};

export default Product;
