import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem("token");

        if (!token) {
          console.error("‚õî No token found, redirecting to login");
          navigate("/login");
          return;
        }

        const response = await fetch("http://localhost:8002/api/view-user-profile", {
          method: "GET",
          headers: {
            "Authorization": `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        const data = await response.json();
        console.log("üì¢ API Response:", data);

        if (!response.ok) {
          if (response.status === 401 || response.status === 403) {
            console.error("‚õî Unauthorized - Redirecting to login");
            localStorage.removeItem("token");
            navigate("/login");
          }
          throw new Error(data.message || "Failed to fetch profile");
        }

        if (!data.profile) {
          throw new Error("üö® Profile data missing in API response");
        }

        setUser(data.profile);
      } catch (err) {
        console.error("‚ùå Error fetching profile:", err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [navigate]); // Keeping navigate as dependency

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h2>User Profile</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {user ? (
        <div>
          <img
            src={user.profileImage?.startsWith("http") ? user.profileImage : `http://localhost:8002${user.profileImage}`}
            alt="Profile"
            onError={(e) => (e.target.src = "https://via.placeholder.com/120")}
          />
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Username:</strong> {user.username}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Gender:</strong> {user.gender || "Not specified"}</p>
        </div>
      ) : (
        <p>No profile data available</p>
      )}
    </div>
  );
};

export default UserProfile;
