import React, { useState } from "react";

const Blog = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [author, setAuthor] = useState("");
  const [image, setImage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const blogSubmitData = async (e) => {
    e.preventDefault();
    try {
      const result = await fetch("http://localhost:8002/api/add-blog", {
        method: "POST",
        body: JSON.stringify({ title, description, author, image }),
        headers: {
          "Content-Type": "application/json",
        },
      });
      if (result.ok) {
        setSuccessMessage("Your blog has been added successfully!");
        setTitle("");
        setDescription("");
        setAuthor("");
        setImage("");
      } else {
        setSuccessMessage("There was an error adding your blog. Please try again.");
      }
    } catch (error) {
      console.error("Error:", error);
      setSuccessMessage("An error occurred. Please try again later.");
    }
  };

  return (
    <div>
      <h2>Blog Page</h2>
      <form onSubmit={blogSubmitData}>
        <div>
          <label>Title:</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </div>
        <div>
          <label>Description:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
        </div>
        <div>
          <label>Author:</label>
          <input type="text" value={author} onChange={(e) => setAuthor(e.target.value)} required />
        </div>
        <div>
          <label>Image URL:</label>
          <input type="text" value={image} onChange={(e) => setImage(e.target.value)} />
        </div>
        <button type="submit">Submit Blog</button>
      </form>
      {successMessage && <p>{successMessage}</p>}
    </div>
  );
};

export default Blog;
