import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem("token");

        if (!token) {
          console.error("No token found, redirecting to login");
          navigate("/login");
          return;
        }

        const response = await fetch("http://localhost:8002/api/view-user-profile", {
          method: "GET",
          headers: {
            "Authorization": `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        // Read response as text first (for debugging)
        const textResponse = await response.text();
        console.log("üîç Raw API Response:", textResponse);

        // Parse JSON response
        const data = JSON.parse(textResponse);

        if (response.ok) {
          console.log("‚úÖ Profile Data:", data);
          setUser(data.profile);
          setError("");
        } else {
          throw new Error(data.message || "Failed to fetch profile");
        }
      } catch (err) {
        console.error("‚ùå Profile fetch error:", err.message);
        setError(err.message || "Error loading profile");

        if (err.message.includes("401") || err.message.includes("403")) {
          localStorage.removeItem("token");
          navigate("/login");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [navigate]);

  if (loading) {
    return <div className="loading-spinner">Loading...</div>;
  }

  return (
    <div className="profile-container">
      <h2>User Profile</h2>
      {error && <p className="error-message">{error}</p>}

      {user ? (
        <div className="profile-card">
          <img
            src={
              user.profileImage
                ? `http://localhost:8002${user.profileImage}`
                : "https://via.placeholder.com/120"
            }
            alt="Profile"
            className="profile-image"
            onError={(e) => {
              e.target.src = "https://via.placeholder.com/120";
            }}
          />
          <div className="profile-info">
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Username:</strong> {user.username}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Gender:</strong> {user.gender || "Not specified"}</p>
          </div>
        </div>
      ) : (
        <p className="no-profile">No profile data available</p>
      )}
    </div>
  );
};

export default UserProfile;
