import React, { useState, useEffect } from "react";
const Product = () => {
  const [portfolio, setPortfolio] = useState([]); 

  const showProduct = async () => {

      const portfolioApi = await fetch(`https://dummyjson.com/products`);
      const fetchData = await portfolioApi.json(); 
          if(fetchData.ok){
            setPortfolio(fetchData.products); 
           } else{
              console.log("Error fetching products:");
      }
  };
  useEffect(() => {
    showProduct();
  }, []);
  return (
    <div className="product-outer">
        <h2>Product Details</h2>
        <div className="product-list">
          {portfolio.map((productItem, i) => (
            <div className="product-inner">
            <div className="product-title-dec">
              <h3>{productItem?.title}</h3>
              <p>{productItem.price}</p>

            </div>
            <p className="portfolio-description">{productItem?.description}</p>
            </div>
          ))}
      </div>
    </div>
  );
};
export default Product;
