import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import Users from './Users';
import Products from './Products';
import AddBlog from './AddBlog';
import '../assets/dashbord.css';

const Dashboard = () => {
  const [activeSection, setActiveSection] = useState('home');
  const navigate = useNavigate(); // Initialize useNavigate
  const [user, setUser] = useState(null);

  const handleProfileOption = (event) => {
    const selectedOption = event.target.value;
    if (selectedOption === 'view-profile') {
      navigate('/profile'); // Navigate to the UserProfile page
    } else if (selectedOption === 'logout') {
      // Handle logout (clear localStorage and redirect to login)
      localStorage.removeItem('user');
      navigate('/login');
    }
  };

  const renderContent = () => {
    switch (activeSection) {
      case 'users':
        return <Users />;
      case 'products':
        return <Products />;
      case 'blog':
        return <AddBlog />;
      default:
        return (
          <div className="main-content">
            <h2>Welcome to the Admin Panel</h2>
            <p>Select an option from the sidebar to get started.</p>
          </div>
        );
    }
  };
  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) return;

        const response = await fetch("http://localhost:8002/api/profile", {
          method: "GET",
          headers: { "Authorization": token, "Content-Type": "application/json" },
        });

        const data = await response.json();
        if (response.ok) {
          setUser(data.profile);
        }
      } catch (err) {
        console.error("Error fetching profile", err);
      }
    };

    fetchUserProfile();
  }, []);

  return (
    <div className="admin-panel">
      <header>
        <div className="logosec">
          <div className="logo">Admin Dashboard</div>
        </div>
        <div className="message">
          <div className="circle"></div>
          <img 
            src="https://media.geeksforgeeks.org/wp-content/uploads/20221210183322/8.png"
            className="icn"
            alt="notifications"
          />
          <div className="dp user-profile">
            <img 
              src="https://themewagon.github.io/deskapp2/vendors/images/photo1.jpg"
              className="dpicn"
              alt="profile"
            />
            <select onChange={handleProfileOption}>
              <option value="">Options</option>
              <option value="view-profile">View Profile</option>
              <option value="logout">Logout</option>
            </select>
          </div>
        </div>
      </header>

      {/* Sidebar Navigation */}
      <div className="navcontainer">
        <nav className="nav">
          <div className="nav-upper-options">
            <div 
              className={`nav-option ${activeSection === 'home' ? 'active' : ''}`} 
              onClick={() => setActiveSection('home')}
            >
              <img 
                src="https://media.geeksforgeeks.org/wp-content/uploads/20221210182148/Untitled-design-(29).png"
                className="nav-img"
                alt="dashboard"
              />
              <h3>Dashboard</h3>
            </div>

            <div 
              className={`nav-option ${activeSection === 'users' ? 'active' : ''}`} 
              onClick={() => setActiveSection('users')}
            >
              <img 
                src="https://media.geeksforgeeks.org/wp-content/uploads/20221210183323/10.png"
                className="nav-img"
                alt="user"
              />
              <h3>User</h3>
            </div>

            <div 
              className={`nav-option ${activeSection === 'blog' ? 'active' : ''}`} 
              onClick={() => setActiveSection('blog')}
            >
              <img 
                src="https://media.geeksforgeeks.org/wp-content/uploads/20221210183323/10.png"
                className="nav-img"
                alt="user"
              />
              <h3>Add Blog</h3>
            </div>
            <div 
              className={`nav-option ${activeSection === 'products' ? 'active' : ''}`} 
              onClick={() => setActiveSection('products')}
            >
              <img 
                src="https://cdn-icons-png.flaticon.com/512/2907/2907764.png"
                className="nav-img"
                alt="products"
              />
              <h3>Products</h3>
            </div>
          </div>
        </nav>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {renderContent()}
      </div>
    </div>
  );
};

export default Dashboard;
