import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
const Portfolio = () => {
const [portfolio, setPortfolio] = useState([]);
const showPortfolio = async()=>{
  const portfolioApi = await fetch(`http://localhost:8000/api/portfolioview/`);
  const fetchData = await portfolioApi.json();
  setPortfolio(fetchData);
}
const downloadCSV = () => {
  if (!portfolio.length) {
    alert("No portfolio data available to download.");
    return;
  }

  // Prepare CSV content
  const headers = ["_id", "title", "description", "portfoliolink"];
  const rows = portfolio.map((item) => [
    item._id,
    item.title,
    item.description,
    item.portfoliolink,
  ]);

  const escapeCsvField = (field) => {
    if (typeof field === "string") {
      return `"${field.replace(/"/g, '""')}"`; // Escape quotes and wrap in double quotes
    }
    return field;
  };

  const csvContent = [
    headers.map(escapeCsvField).join(","), // Escape headers
    ...rows.map((row) => row.map(escapeCsvField).join(",")), // Escape each field in rows
  ].join("\n");

  // Create a Blob from the CSV content
  const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
  const url = URL.createObjectURL(blob);

  // Create a temporary link to trigger the download
  const link = document.createElement("a");
  link.href = url;
  link.setAttribute("download", "portfolio.csv");
  link.style.display = "none";
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};

useEffect(() => {
  showPortfolio();
}, []);
  return (
    <div className="portfolio">
      <header>
        <h1 className="portfolio_title">My Portfolio</h1>
        <p className="portfolio-desc">Welcome to my portfolio! I am a web developer specializing in Shopify Plus.</p>
      </header>
      <div className="projects">
        <h2>Projects</h2>
        <div className="project-list">
          {portfolio.map((portfolioItem, i)=>(
              <div className="project project-inner" key={i}>
              <img src={`http://localhost:8000/${portfolioItem.image}`} alt={portfolioItem?.title} className="project-image"/>
                <h3>{portfolioItem?.title}</h3>
                <p className="portfolio-description">{portfolioItem?.description}</p>
                <div className="btn-wrapper">
                <Link target={"_blank"} to={`/portfolio-details/${portfolioItem._id}`} rel="noopener noreferrer">
                  View Project
                </Link>
                <a className="live-demo-link" target={"_blank"} href={portfolioItem.portfoliolink} rel="noopener noreferrer">
                  Live Demo
                </a>
                </div>
              </div>
            ))}
        </div>
        <button className="portfolio-csv" onClick={downloadCSV}>Download Portfolio CSV </button>
      </div>
      <footer>
        <h2>Contact Us</h2>
        <p>Email: info@tech-prastish.com</p>
      </footer>
    </div>
  );
};
export default Portfolio;
