import { BrowserRouter, Routes ,Route } from 'react-router-dom';
import Nav from '../components/Nav';
import Portfolio from '../pages/Portfolio';
import ContactUs from '../pages/Contact';
import AboutUs from '../pages/About';
import NoPage from '../pages/NoPage';
import Product from '../pages/Product';
import Blog from '../pages/Blog';
import BlogDetails from '../pages/blogDetails';
import PortfolioDetails from '../pages/PortfolioDetails';
import Dashboard from '../admin/Dashboard';
import SignUp from '../components/SignUp';
import Login from '../components/Login';
import ResetPassword from '../components/ResetPasswordPage';
import ForgotPasswordPage from '../components/ForgotPassword';
import Footer from '../components/Footer';
import CreatePortfolio from '../admin/CreatePortfolio';
import ProtectedRoute from '../components/ProtectedRoute';
const Router = ()=>{
return(
<BrowserRouter>
 <Nav />
   <Routes>
  <Route path="/signup" element={<SignUp />} />
  <Route path="/login" element={<Login />} />
  <Route path="/forgot-password" element={<ForgotPasswordPage />} />
  <Route path="/reset-password" element={<ResetPassword />} />
  <Route path="/portfolio-details/:id" element={<PortfolioDetails />} />
  <Route path="/" element={ <ProtectedRoute> <Portfolio/></ProtectedRoute>}/>
  <Route path="/contact" element={<ProtectedRoute><ContactUs /></ProtectedRoute>}/>
  <Route path="/blogs" element={<ProtectedRoute><Blog/></ProtectedRoute>}/>
  <Route path="/blog-details/:id" element={<ProtectedRoute><BlogDetails/></ProtectedRoute>}/>
  <Route path="/about" element={ <ProtectedRoute><AboutUs /></ProtectedRoute>}/>
  <Route path="/product" element={<ProtectedRoute><Product/></ProtectedRoute>} />
  <Route path="*" element={<NoPage />} />

  {/* admin panel */}

  <Route path="/admin/dashbord" element={<ProtectedRoute allowedRoles={['admin']}> <Dashboard/></ProtectedRoute>}/>
  <Route path="/admin/dashbord/create-portfolio" element={ <ProtectedRoute> <CreatePortfolio /></ProtectedRoute>} />
</Routes>
<Footer />
</BrowserRouter>
    )
}
export default Router;
